const bdeat_AttributeInfo *BdemDecoderOptions::lookupAttributeInfo(
        const char *name,
        int         nameLength)
{
    switch(nameLength) {
        case 8: {
            if ((name[0]|0x20)=='m'
             && (name[1]|0x20)=='a'
             && (name[2]|0x20)=='x'
             && (name[3]|0x20)=='d'
             && (name[4]|0x20)=='e'
             && (name[5]|0x20)=='p'
             && (name[6]|0x20)=='t'
             && (name[7]|0x20)=='h')
            {
                return &ATTRIBUTE_INFO_ARRAY[ATTRIBUTE_INDEX_MAX_DEPTH];
            }
        } break;
        case 11: {
            if ((name[0]|0x20)=='b'
             && (name[1]|0x20)=='d'
             && (name[2]|0x20)=='e'
             && (name[3]|0x20)=='m'
             && (name[4]|0x20)=='v'
             && (name[5]|0x20)=='e'
             && (name[6]|0x20)=='r'
             && (name[7]|0x20)=='s'
             && (name[8]|0x20)=='i'
             && (name[9]|0x20)=='o'
             && (name[10]|0x20)=='n')
            {
                return &ATTRIBUTE_INFO_ARRAY[ATTRIBUTE_INDEX_BDEM_VERSION];
            }
        } break;
    }
    return 0;
}

const bdeat_AttributeInfo *BdemDecoderOptions::lookupAttributeInfo(int id)
{
    switch (id) {
      case ATTRIBUTE_ID_BDEM_VERSION:
        return &ATTRIBUTE_INFO_ARRAY[ATTRIBUTE_INDEX_BDEM_VERSION];
      case ATTRIBUTE_ID_MAX_DEPTH:
        return &ATTRIBUTE_INFO_ARRAY[ATTRIBUTE_INDEX_MAX_DEPTH];
      default:
        return 0;
    }
}

// ACCESSORS

bsl::ostream& BdemDecoderOptions::print(
    bsl::ostream& stream,
    int           level,
    int           spacesPerLevel) const
{
    if (level < 0) {
        level = -level;
    }
    else {
        bdlb::Print::indent(stream, level, spacesPerLevel);
    }

    int levelPlus1 = level + 1;

    if (0 <= spacesPerLevel) {
        // multiline

        stream << "[\n";

        bdlb::Print::indent(stream, levelPlus1, spacesPerLevel);
        stream << "BdemVersion = ";
        bdlb::PrintMethods::print(stream, d_bdemVersion,
                                 -levelPlus1, spacesPerLevel);

        bdlb::Print::indent(stream, levelPlus1, spacesPerLevel);
        stream << "MaxDepth = ";
        bdlb::PrintMethods::print(stream, d_maxDepth,
                                 -levelPlus1, spacesPerLevel);

        bdlb::Print::indent(stream, level, spacesPerLevel);
        stream << "]\n";
    }
    else {
        // single line

        stream << '[';

        stream << ' ';
        stream << "BdemVersion = ";
        bdlb::PrintMethods::print(stream, d_bdemVersion,
                                 -levelPlus1, spacesPerLevel);

        stream << ' ';
        stream << "MaxDepth = ";
        bdlb::PrintMethods::print(stream, d_maxDepth,
                                 -levelPlus1, spacesPerLevel);

        stream << " ]";
    }

    return stream << bsl::flush;
}
